mapper.insert:

insert into contract (contractAddress, blockHash, transactionHash,
blockNumber, creator, createTime,
status, totalSupply, ercName,
ercSymbol, decimals, abi
)
values (#{contractaddress,jdbcType=VARCHAR}, #{blockhash,jdbcType=VARCHAR}, #{transactionhash,jdbcType=VARCHAR},
#{blocknumber,jdbcType=DECIMAL}, #{creator,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP},
#{status,jdbcType=INTEGER}, #{totalsupply,jdbcType=BIGINT}, #{ercname,jdbcType=VARCHAR},
#{ercsymbol,jdbcType=VARCHAR}, #{decimals,jdbcType=INTEGER}, #{abi,jdbcType=LONGVARCHAR}
)
